// Pagina1.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Pagina1.css';

const Pagina1 = () => {
  const navigate = useNavigate();
  const [matricula, setMatricula] = useState('');
  const [senha, setSenha] = useState('');
  const [exibirSenha, setExibirSenha] = useState(false);

  const realizarLogin = () => {
    // L√≥gica para realizar o login
    if (matricula === '261944' && senha === '1944') {
      // Redireciona para a Pagina2 se a matr√≠cula e senha estiverem corretas
      navigate('/pagina2');
    } else {
      alert('Login ou senha inv√°lidos.');
    }
  };

  const toggleExibirSenha = () => {
    setExibirSenha(!exibirSenha);
  };

  return (
    <div className="container">
      <div className="form-container">
        <h1>Moeda Social</h1>
        <form action="#" method="post" id="loginForm">
          <label htmlFor="matricula">Matr√≠cula:</label>
          <input type="text" id="matricula" name="matricula" value={matricula} onChange={(e) => setMatricula(e.target.value)} required />

          <label htmlFor="senha">Senha:</label>
          <div className="password-container">
            <input
              type={exibirSenha ? 'text' : 'password'}
              id="senha"
              name="senha"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              required
            />
            <i className={`toggle-password ${exibirSenha ? 'exibir' : 'ocultar'}`} onClick={toggleExibirSenha}>
              {exibirSenha ? 'üôà' : 'üëÅÔ∏è'}
            </i>
          </div>

          <button type="button" onClick={realizarLogin}>
            Login
          </button>
        </form>

        <p>
          N√£o tem uma conta? <Link to="/cadastro" id="inscrevaSeLink">Inscreva-se</Link>
        </p>
      </div>
    </div>
  );
};

export default Pagina1;
